import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createRide } from '../services/api';
import '../styles/PublishRide.css';

const PublishRide = ({ user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    origin: '',
    destination: '',
    date: '',
    time: '',
    availableSeats: 1,
    pricePerSeat: '',
    description: '',
    vehicleDetails: {
      model: '',
      licensePlate: ''
    },
    preferences: {
      music: false,
      smoking: false,
      petFriendly: false,
      genderPreference: 'any'
    }
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Validate form data
      if (!formData.origin.trim() || !formData.destination.trim()) {
        throw new Error('Please enter both origin and destination');
      }

      if (!formData.date || !formData.time) {
        throw new Error('Please select both date and time');
      }

      if (formData.availableSeats < 1) {
        throw new Error('Please enter at least 1 available seat');
      }

      if (formData.pricePerSeat < 0) {
        throw new Error('Price per seat cannot be negative');
      }

      // Combine date and time
      const dateTime = new Date(`${formData.date}T${formData.time}`);
      if (dateTime < new Date()) {
        throw new Error('Cannot publish ride for past date/time');
      }

      // Prepare ride data
      const rideData = {
        origin: formData.origin.trim(),
        destination: formData.destination.trim(),
        date: dateTime.toISOString(),
        availableSeats: parseInt(formData.availableSeats),
        pricePerSeat: parseFloat(formData.pricePerSeat),
        description: formData.description.trim(),
        driver: user.id
      };

      // Create ride
      await createRide(rideData);
      navigate('/');

    } catch (err) {
      console.error('Publish ride error:', err);
      setError(err.message || 'Failed to publish ride. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const today = new Date().toISOString().split('T')[0];

  return (
    <div className="publish-container">
      <div className="publish-card">
        <h2>Publish a Ride</h2>
        <form onSubmit={handleSubmit} className="publish-form">
          <div className="form-group">
            <label htmlFor="origin">Leaving From</label>
            <input
              id="origin"
              type="text"
              name="origin"
              value={formData.origin}
              onChange={handleChange}
              placeholder="Enter city or location"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="destination">Going To</label>
            <input
              id="destination"
              type="text"
              name="destination"
              value={formData.destination}
              onChange={handleChange}
              placeholder="Enter destination"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="date">Date</label>
              <input
                id="date"
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
                min={today}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="time">Time</label>
              <input
                id="time"
                type="time"
                name="time"
                value={formData.time}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="availableSeats">Available Seats</label>
              <input
                id="availableSeats"
                type="number"
                name="availableSeats"
                value={formData.availableSeats}
                onChange={handleChange}
                min="1"
                max="8"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="pricePerSeat">Price per Seat (â‚¹)</label>
              <input
                id="pricePerSeat"
                type="number"
                name="pricePerSeat"
                value={formData.pricePerSeat}
                onChange={handleChange}
                min="0"
                step="0.01"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="description">Description (Optional)</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Add any additional details about the ride"
              rows="3"
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button 
            type="submit" 
            className="publish-button" 
            disabled={loading}
          >
            {loading ? 'Publishing...' : 'Publish Ride'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default PublishRide;
