import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import LocationMap from '../components/LocationMap';
import '../styles/SearchRide.css';

const SearchRide = () => {
  const navigate = useNavigate();
  const [searchData, setSearchData] = useState({
    origin: '',
    destination: '',
    date: '',
    seats: 1,
    pickupLat: '',
    pickupLng: '',
    dropLat: '',
    dropLng: ''
  });
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Handle pickup location selection
  const handlePickupSelect = (location) => {
    setSearchData(prev => ({
      ...prev,
      origin: location.name,
      pickupLat: location.lat,
      pickupLng: location.lng
    }));
  };

  // Handle drop location selection
  const handleDropSelect = (location) => {
    setSearchData(prev => ({
      ...prev,
      destination: location.name,
      dropLat: location.lat,
      dropLng: location.lng
    }));
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setSearchData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Search for rides
  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      const queryParams = new URLSearchParams({
        ...searchData
      }).toString();

      const response = await fetch(`/api/rides/search?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to search rides');
      }

      setSearchResults(data.rides);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Request to join a ride
  const handleJoinRequest = async (rideId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/rides/join/${rideId}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message);
      }

      // Update the UI to show request sent
      setSearchResults(prev => 
        prev.map(ride => 
          ride._id === rideId 
            ? { ...ride, requestSent: true }
            : ride
        )
      );
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="search-ride-container">
      <h2>Search for Rides</h2>

      <form onSubmit={handleSearch} className="search-form">
        <div className="location-maps">
          <div className="map-container">
            <label>Pickup Location</label>
            <LocationMap 
              onLocationSelect={handlePickupSelect}
              placeholder="Enter pickup location"
            />
          </div>

          <div className="map-container">
            <label>Drop Location</label>
            <LocationMap 
              onLocationSelect={handleDropSelect}
              placeholder="Enter drop location"
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Date</label>
            <input
              type="datetime-local"
              name="date"
              value={searchData.date}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Number of Seats</label>
            <input
              type="number"
              name="seats"
              min="1"
              max="6"
              value={searchData.seats}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <button type="submit" className="search-button" disabled={loading}>
          {loading ? 'Searching...' : 'Search Rides'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      <div className="search-results">
        {searchResults.length > 0 ? (
          <div className="rides-list">
            {searchResults.map(ride => (
              <div key={ride._id} className="ride-card">
                <div className="ride-info">
                  <div className="driver-info">
                    <h3>Driver: {ride.creator.fullName}</h3>
                    <p>Gender: {ride.creator.gender}</p>
                  </div>

                  <div className="ride-details">
                    <p><strong>From:</strong> {ride.pickupLocation.name}</p>
                    <p><strong>To:</strong> {ride.dropLocation.name}</p>
                    <p><strong>Date:</strong> {new Date(ride.departureTime).toLocaleString()}</p>
                    <p><strong>Available Seats:</strong> {ride.availableSeats}</p>
                    <p><strong>Match Score:</strong> {ride.score}%</p>
                  </div>

                  {ride.requestSent ? (
                    <button className="request-sent-button" disabled>
                      Request Sent
                    </button>
                  ) : (
                    <button
                      className="join-button"
                      onClick={() => handleJoinRequest(ride._id)}
                    >
                      Request to Join
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        ) : (
          searchData.origin && searchData.destination && !loading && 
          <p className="no-results">No rides found matching your criteria</p>
        )}
      </div>
    </div>
  );
};

export default SearchRide;
